@dataclass
class EvaluationResult:
    framework: str
    metrics: Dict[str, float]
    individual_scores: pd.DataFrame
    execution_time: float
    timestamp: datetime
    metadata: Dict[str, Any]

class MultiFrameworkRAGEvaluator:
    """Production-ready RAG evaluation supporting multiple frameworks (2025)"""

    def __init__(self, config: Dict[str, Any] = None):
        self.config = config or self._get_default_config()
        self.available_frameworks = self._check_framework_availability()
        self.evaluation_history = []

    def _get_default_config(self) -> Dict[str, Any]:
        return {
            'ragas': {
                'llm': 'gpt-4',
                'embeddings': 'text-embedding-3-large',
                'metrics': ['faithfulness', 'answer_relevancy', 'context_precision']
            },
            'phoenix': {
                'project_name': 'rag_evaluation',
                'enable_tracing': True
            },
            'langsmith': {
                'project_name': 'rag_eval_2025',
                'evaluators': ['correctness', 'relevance', 'groundedness']
            },
            'evaluation': {
                'timeout': 300,
                'batch_size': 10,
                'parallel_execution': True
            }
        }

    def _check_framework_availability(self) -> Dict[str, bool]:
        return {
            'ragas': RAGAS_AVAILABLE,
            'phoenix': PHOENIX_AVAILABLE,
            'langsmith': LANGSMITH_AVAILABLE
        }

    async def evaluate_with_ragas(self, 
                                questions: List[str],
                                answers: List[str], 
                                contexts: List[List[str]],
                                ground_truths: List[str] = None) -> EvaluationResult:
        """Evaluate using RAGAS framework"""
        if not self.available_frameworks['ragas']:
            raise RuntimeError("RAGAS framework not available. Install with: pip install ragas")

        start_time = datetime.now()

        from datasets import Dataset

        # Prepare dataset
        eval_data = {
            'question': questions,
            'answer': answers,
            'contexts': contexts
        }

        if ground_truths:
            eval_data['ground_truth'] = ground_truths

        dataset = Dataset.from_dict(eval_data)

        # Configure metrics
        metrics = []
        metric_mapping = {
            'faithfulness': faithfulness,
            'answer_relevancy': answer_relevancy,
            'context_precision': context_precision
        }

        for metric_name in self.config['ragas']['metrics']:
            if metric_name in metric_mapping:
                metrics.append(metric_mapping[metric_name])

        # Run evaluation
        result = evaluate(
            dataset=dataset,
            metrics=metrics,
            llm=self.config['ragas']['llm'],
            embeddings=self.config['ragas']['embeddings']
        )

        # Process results
        df = result.to_pandas()
        numeric_columns = df.select_dtypes(include=[np.number]).columns
        overall_metrics = df[numeric_columns].mean().to_dict()

        execution_time = (datetime.now() - start_time).total_seconds()

        return EvaluationResult(
            framework='ragas',
            metrics=overall_metrics,
            individual_scores=df,
            execution_time=execution_time,
            timestamp=start_time,
            metadata={'version': '2.0', 'model': self.config['ragas']['llm']}
        )

