    async def run_comprehensive_evaluation(self,
                                         questions: List[str],
                                         answers: List[str],
                                         contexts: List[List[str]],
                                         ground_truths: List[str] = None,
                                         frameworks: List[str] = None) -> Dict[str, EvaluationResult]:
        """Run evaluation across multiple frameworks"""

        if frameworks is None:
            frameworks = [fw for fw, available in self.available_frameworks.items() if available]

        results = {}
        tasks = []

        for framework in frameworks:
            if framework == 'ragas' and self.available_frameworks['ragas']:
                tasks.append(('ragas', self.evaluate_with_ragas(
                    questions, answers, contexts, ground_truths
                )))
            elif framework == 'phoenix' and self.available_frameworks['phoenix']:
                tasks.append(('phoenix', self.evaluate_with_phoenix(
                    questions, answers, contexts
                )))
            elif framework == 'langsmith' and self.available_frameworks['langsmith']:
                tasks.append(('langsmith', self.evaluate_with_langsmith(
                    questions, answers, contexts
                )))

        # Execute evaluations
        if self.config['evaluation']['parallel_execution']:
            # Parallel execution
            completed_tasks = await asyncio.gather(
                *[task[1] for task in tasks],
                return_exceptions=True
            )

            for (framework_name, _), result in zip(tasks, completed_tasks):
                if isinstance(result, Exception):
                    print(f"Error evaluating with {framework_name}: {result}")
                else:
                    results[framework_name] = result
        else:
            # Sequential execution
            for framework_name, task in tasks:
                try:
                    result = await task
                    results[framework_name] = result
                except Exception as e:
                    print(f"Error evaluating with {framework_name}: {e}")

        # Store evaluation history
        self.evaluation_history.append({
            'timestamp': datetime.now(),
            'frameworks': list(results.keys()),
            'num_samples': len(questions),
            'results_summary': {fw: res.metrics for fw, res in results.items()}
        })

        return results

    def generate_comparison_report(self, 
                                 evaluation_results: Dict[str, EvaluationResult],
                                 output_path: str = "multi_framework_comparison.json") -> str:
        """Generate comprehensive comparison report"""

        report = {
            'evaluation_summary': {
                'timestamp': datetime.now().isoformat(),
                'frameworks_compared': list(evaluation_results.keys()),
                'total_samples': len(evaluation_results[list(evaluation_results.keys())[0]].individual_scores)
            },
            'performance_comparison': {},
            'execution_times': {},
            'recommendations': []
        }

        # Compare metrics across frameworks
        all_metrics = set()
        for result in evaluation_results.values():
            all_metrics.update(result.metrics.keys())

        for metric in all_metrics:
            report['performance_comparison'][metric] = {}
            for framework, result in evaluation_results.items():
                if metric in result.metrics:
                    report['performance_comparison'][metric][framework] = result.metrics[metric]

        # Execution time comparison
        for framework, result in evaluation_results.items():
            report['execution_times'][framework] = result.execution_time

        # Generate recommendations
        if len(evaluation_results) > 1:
            fastest_framework = min(evaluation_results.items(), key=lambda x: x[1].execution_time)[0]
            report['recommendations'].append(
                f"Fastest framework: {fastest_framework} ({evaluation_results[fastest_framework].execution_time:.2f}s)"
            )

            # Find framework with highest average metric scores
            framework_avg_scores = {}
            for framework, result in evaluation_results.items():
                framework_avg_scores[framework] = np.mean(list(result.metrics.values()))

            best_performing = max(framework_avg_scores.items(), key=lambda x: x[1])[0]
            report['recommendations'].append(
                f"Highest average scores: {best_performing} (avg: {framework_avg_scores[best_performing]:.3f})"
            )

        # Save report
        with open(output_path, 'w', encoding='utf-8') as f:
            json.dump(report, f, indent=2, default=str)

        return output_path

# Usage Example
async def main():
    # Sample data
    questions = [
        "What is machine learning?",
        "How does neural network training work?",
        "What are the applications of AI in healthcare?"
    ]

    answers = [
        "Machine learning is a subset of AI that enables computers to learn patterns from data.",
        "Neural networks learn by adjusting weights through backpropagation during training.",
        "AI in healthcare includes medical imaging, drug discovery, and diagnostic assistance."
    ]

    contexts = [
        ["ML algorithms learn from data to make predictions", "AI encompasses various learning techniques"],
        ["Backpropagation updates network weights", "Training involves forward and backward passes"],
        ["AI assists doctors in diagnosis", "Medical AI analyzes images and data"]
    ]

    # Initialize evaluator
    evaluator = MultiFrameworkRAGEvaluator()

    # Run comprehensive evaluation
    results = await evaluator.run_comprehensive_evaluation(
        questions=questions,
        answers=answers,
        contexts=contexts
    )

    # Generate comparison report
    report_path = evaluator.generate_comparison_report(results)

    print(f"Multi-framework evaluation completed. Available frameworks: {list(results.keys())}")
    print(f"Comparison report saved to: {report_path}")

    # Display summary
    for framework, result in results.items():
        print(f"\n{framework.upper()} Results:")
        for metric, score in result.metrics.items():
            print(f"  {metric}: {score:.3f}")
        print(f"  Execution time: {result.execution_time:.2f}s")

    return results

if __name__ == "__main__":
    results = asyncio.run(main())
